function highlightErrorLine(errorLine) {
    var m = errorLine.full.match(/at ([A-Za-z0-0$]+)\.([A-Za-z0-0$]+)/)
    if (!m) return;
    var methodName = m[2]

    var m2 = errorLine.full.match(/\<anonymous\>\:(\d+):(\d+)/)
    if (m2) {
        var textMorph = this.get('ObjectEditorScriptPane')
        var offset = Strings.lines(textMorph.textString).slice(0, m2[1]).inject(0,
                        function(sum, ea) { return ea.length + sum + 1});
        textMorph.highlightFromTo(
           Number(offset),Number(offset + m2[2]))
    }
    // if (methodName == this.get('ObjectEditorScriptList').selection) {
        this.get('ObjectEditorScriptPane')
    // }
}

