function renumber() {
    var text = this.textString

    var primCounter = 0
    var secCounter = 0
    var terCounter =0

    var p = this.patterns()
    this.textString = Strings.lines(text)
        .collect(function(ea) {

            if(ea.match(p.primEx)) {
                secCounter = 0
                terCounter = 0
                return ea.replace(p.primEx, "" + ++primCounter + ". ")
            }
            if(ea.match(p.secEx)) {
                terCounter = 0;
                return ea.replace(p.secEx, "" + primCounter + "." + ++secCounter + ". ")
            }
            if(ea.match(p.terEx)) {
                return ea.replace(p.terEx, "" + primCounter + "." 
                    + secCounter + "."
                    + ++terCounter + ". ")
            }

            return ea
        })
        .join("\n")

}

