function format() {
    var m = this;
    var p = this.patternsFullLine()
    var textReg = /(?:^|\n)[A-Za-z].*/g
    m.emphasizeAll({fontFamily: 'Helvetica', color: Color.darkGray, fontSize: 10})
    m.emphasizeRegex(p.primEx, {fontSize: 16, color: CrayonColors.ocean})
    m.emphasizeRegex(p.secEx, {fontSize: 14, color: CrayonColors.ocean})
    m.emphasizeRegex(p.terEx, {fontSize: 12, color: CrayonColors.ocean})
    m.emphasizeRegex(textReg, {fontSize: 10, color: CrayonColors.darkGray, left: 10})
    m.emphasizeRegex(/\n/g, {fontSize: 10, })


    // ok, lets hack it!
    this.textChunks.each(function(chunk) {
        chunk.chunkNode.style.position = "relative"
        chunk.chunkNode.style.lineHeight= "100%"
        chunk.chunkNode.style.top= "10px"
        var s = chunk.textString
        var offset = ""
        if (s.match(p.primEx)) {
            chunk.chunkNode.style.lineHeight= "200%"
            chunk.chunkNode.style.top= "15px"
            offset = "0px";
        }
        if (s.match(p.secEx)) {
            chunk.chunkNode.style.lineHeight= "150%"
            chunk.chunkNode.style.top= "10px"
            offset = "10px";
        }
        if (s.match(p.terEx)) offset = "20px";
        if (s.match(textReg)) {
            offset = "30px";
        }

        chunk.chunkNode.style.left = offset
    })

}

