function selectedItemChanged(item, oldItem) {
    if (!item) return;
    if (oldItem) { 
        oldItem.submorphs.clone().invoke('remove')
        oldItem.addMorph(oldItem.slide);
        oldItem.slide.disableEvents();
        oldItem.slide.setPosition(pt(10,10));
        oldItem.slide.setScale(1)
    }

    var projector = this.get("SlideProjector")
    if (projector) {
        projector.projectSlide(item.slide)
        item.slide.setScale(1);
        // var label = new TextMorph(new Rectangle(0,0,700,800), "currently displayed");
        // label.applyStyle({fill: null, borderColor: null, fontSize: 50});
        // label.setPosition(pt(200,200))
        // item.addMorph(label)

        var thumb = item.slide.duplicate();
        item.addMorph(thumb)
        thumb.ignoreEvents();
        thumb.disableEvents();
        thumb.setPosition(pt(10,10))    

    }
}

