function deleteCurrentSlide() {
    var selectedItem = this.selectedItem    

    $world.confirm("delete " + selectedItem.slide.name, function(bool) {
        if(bool) {
            var index = this.get('Container').submorphs.indexOf(selectedItem)
            this.selectedItem.remove();
            var nextItem = this.get('Container').submorphs[index];
            if (nextItem) {
                this.selectItem(nextItem)    
                this.scrollToItem(nextItem)   
            }
        }
    }.bind(this))
}

