function onDragStart(evt) {
    var absToRelativePoint =  function (r, absPt) {
		return new Point(
			(absPt.x - r.x) / r.width,
			(absPt.y - r.y) / r.height)
	}
    // absToRelativePoint(new Rectangle(10,10,100,100), pt(20,20))
    // absToRelativePoint(new Rectangle(10,10,100,100), pt(0,0))
    // absToRelativePoint(new Rectangle(-100,-100,200,200), pt(0,0))


    this.dragStartPoint = evt.mousePoint;
    this.originalTargetBounds= this.owner.bounds();
    this.relativeOrigin =  absToRelativePoint(pt(0,0).extent(this.owner.getExtent()), this.owner.getOrigin())
}

