function loadBibTexLib() {
    module('lib.Bibtex').load(true);
    var webR = new WebResource(this.getBibTexURL());
    webR.get();
    var parser = new lib.Bibtex.BibtexParser();

    // this.get('BibtexSource').textString = webR.content
    // var content =  this.get('BibtexSource').textString
    var content =  webR.content
    parser.setInput(content);
    try {
        parser.bibtex()
    } catch(e) {
        alertOK("Bibtex Parsing Error: " + e)
    }
    var display = new lib.Bibtex.BibtexDisplay()
    var entries = parser.getEntries();

    // fix all values
    Properties.own(this.entries).forEach(function(ea) {
        var entry = this.entries[ea]
        Properties.own(entry).forEach(function(attrName) {
            entry[attrName] = display.fixValue(entry[attrName])
        })
    }, this)
    this.bibTexEntries = entries;
}

