function openExcerpt(evt) {
    var link = this.getLinkURL()

    if (!link.asWebResource().exists()) {
        this.createExcerptAndOpenExcerpt()
        return
    }

    var worldLoader = this.world().openPartItem(
        "WorldLoaderPane", "PartsBin/Jens")
    worldLoader.get('WorldLoaderPane').navigateTo(link);
    worldLoader.align(
        worldLoader.bounds().topLeft(),
        this.worldPoint(this.shape.bounds().topRight()))


}

