function showBibTex() {
    if (!this.bibTexEntries)
        this.loadBibTexLib()
    var entry = this.bibTexEntries[this.reference]
    var s;
    if (entry) {
        s = this.cleanLatexString(entry.Author) + " (" + entry.Year + ")\n" + 
                this.cleanLatexString(entry.Title) + ".\n";
        if (entry.Journal)
            s += entry.Journal
        if (entry.Booktitle)
            s += entry.Booktitle
    } else {
        s = 'no BibTex entry found for ' + this.reference
    }
    this.get('ReferenceLabel').textString = s
}

