function createExcerptSync() {
    var excerptsURL = this.getExcerptsURL(),
        reference = this.reference,
        sourceURL = excerptsURL.withFilename('template.xhtml'),
        from =  new WebResource(sourceURL).forceUncached(),
        doc = from.get().contentDocument,
        destFileName = reference + ".xhtml",
        destURL = excerptsURL.withFilename(destFileName),
        dest = new WebResource(destURL),
        force = false;

    var world = lively.morphic.World.fromDocument(doc);

    var referenceMorph = world.get('Reference');
    referenceMorph.setTextString('[' + reference + ']');

    if (dest.exists() && !force) {
	this.world().setStatusMessage("did not create " + destFileName + " since it exits",
             Color.red, 5)
    } else {
	this.saveExternalWorld(destURL, world, doc)
	this.world().setStatusMessage("created " + destFileName,  Color.green, 5)
	this.openExcerpt()
    }
}

