430.source:
function makeIdLabel(morph) {
    var idLabel = new lively.morphic.Text(new Rectangle(0,0,300,45),"")
    idLabel.applyStyle({fill: Color.black, textColor: Color.green, borderWidth: 0, 
        opacity: 0.5})
    idLabel.isEpiMorph = true;
    idLabel.isDerivationLabel = true;
    idLabel.textString = '' + morph.name + '\n' + morph.constructor.name + '\n' + 
        'ID: ' + morph.id
    idLabel.target = morph;
    idLabel.addScript(function updatePosition() {
        this.setPosition(this.target.worldPoint(this.target.shape.bounds().center()))
    })
    idLabel.updatePosition();
    lively.bindings.connect(morph, 'globalTransform', idLabel, 'updatePosition')
    idLabel.openInWorld()
    idLabel.ignoreEvents()
    return idLabel
}

