function showDerivationHistoryOfMorph(m) {
    var source = m
    if (!source.derivationIds) return;
    var ids = source.derivationIds.clone()

    // var color = Color.gray.lighter().lighter().lighter()
    var oldLabel = this.makeIdLabel(source)
    while(id = ids.pop()) {
        var target;
        $world.withAllSubmorphsDo(function(ea) {
            if(ea.id == id) { 
                target = ea
            }
        })

        if (target) {
            var label = this.makeIdLabel(target)
            var c = this.makeConnection(oldLabel, label)
            // color = color.darker()
            // c.setBorderColor(color)
            source = target
            target = null;
            oldLabel = label

        }
    }

}

442.source:
function showDerivationHistoryOfMorphHierrachy(morph, depth) {
    this.showDerivationHistoryOfMorph(morph);
    if (depth > 0) {
        morph.submorphs.each(function(ea) {
            this.showDerivationHistoryOfMorphHierrachy(ea, depth - 1)
        }.bind(this))
    }
}

