function loadAllSourceCode() {
    // ok, lets load all code

    this.get("Table").textString = "Load all source"

    var projectRoot = new URL(this.get("ProjectRoot").textString)

    var srcCtrl =  new AnotherSourceDatabase();
    this.sourceDatabase = srcCtrl;
    var progressBar = lively.morphic.World.current().addProgressBar();

    var urls = []
    this.get("Modules").textString.split(/,\w*/).each(function(root) {
        if (!root) return;
        var dirs = new WebResource(projectRoot.withFilename(root)).getSubElements(4).subCollections
        if (dirs) urls.pushAll(dirs.invoke('getURL'))
    })
    progressBar.setLabel("loot at " + urls.length + " directories");

    var files = [];
    var self = this;
    urls.forEachShowingProgress(
        progressBar,
        function(ea) {
            this.get("Table").textString += ea +':';

            var jsFiles = srcCtrl.interestingLKFileNames(ea);
            this.get("Table").textString += jsFiles +'\n';
            files.pushAll(jsFiles); }.bind(this),
        Functions.K, // label func
        function() {
            files = files.uniq();
            files.forEachShowingProgress(
                progressBar,
                function(ea) { 
                cop.withLayers([DisableShowErrorsLayer], function() {
                    try {
                        srcCtrl.addModule(ea)
                    } catch(e) {
                        self.logError(e)
                    }
                })
                },
                Functions.K, // label func
                    function() {
                        progressBar.remove();
                        self.update();
                        alertOK("Load all source FINISHED")
                });
    });


}

