function printTableSortedBy(table, column) {
    // table = this.currentTable
    // column = "FileName"
    if (column) {
        table = table.clone()
        var header = table.shift()
        var pos = header.indexOf(column)
        table = table.sortBy(function(ea) { return ea[pos]})
        if (this.lastSortedColumn == column) {
            this.lastSortedColumn = null
            table = table.reverse()
        } else {
            this.lastSortedColumn = column 
        }
        table.unshift(header)
    }

    var indexedTable = table.collect(function(ea, index) { return [index].concat(ea)})
    if (this.isLatexPrinting()) {
        var label = "tab:" + this.get("TableList").selection
        var caption = this.get("TableList").selection
        if (column) caption += " sorted by " + column
        this.get("Table").textString = this.asLatexTable(indexedTable, label, caption)
    } else {
        this.get("Table").textString = Strings.printTable(indexedTable);
    }
    var s = this.get("Table").textString
    var columnNames = table.first();
    columnNames.each(function(ea) {
        this.get("Table").emphasize(
            {
                fontWeight: 'bold',
                doit: {code: 'this.sortCurrentTableBy("' + ea + '")', context: this}
            },
            s.indexOf(ea), s.indexOf(ea) + ea.length
        )
    }, this)

}

