function computeStatsAllLayers() {
    // COP Layers
    var allLayers = this.allASTElements.select(function(ea) {
        return ea.type == "copDef"
    })

    var propsWithLayerDefs = []

    this.allASTElements.select(function(ea) {
        return (ea.type == "propertyDef")
    }).each(function(ea) {
        var m = ea.getSourceCode().match(/cop\.create\(("|')(.*)("|')\)/)
        if (m) {
            var p = ea.getOwnerPath();
            propsWithLayerDefs.push([" "," ", " ", m[2],
            p.first().fileName,
            p[p.length - 2].name + ">>" +  ea.name])
        }
    })
    propsWithLayerDefs = propsWithLayerDefs.reject(function(ea) {
        return ea[4].match(".tests.")  
    })

    this.tables["AllLayers"] = [[ "Classes", "Methods", "LoC", "LayerName", "FileName"]].pushAll(
        allLayers
        .sortBy(function(ea) {
            return ea.subElements(10).length
        })
        .reject(function(ea) {
            return ea.getOwnerPath().first().fileName.match("cop")
        })
        .reverse()
        .collect(function(ea, index) {
            var subs = ea.subElements(10).reject(function(eaSub) { return eaSub.name.match('beGlobal') })
            var h = this.histogramDict(subs, function(ea) { return ea.type })

            var lines = Strings.lines(ea.getSourceCode()).length
            return [h.copSubElement || 0, h.propertyDef || 0,
                lines , ea.name, ea.getOwnerPath().first().fileName]
        }, this))


        // .concat(propsWithLayerDefs)

}

