function computeStatsCOPUsage() {

    var layerUsage = [];

    this.allASTElements.select(function(ea) {
        return (ea.type == "propertyDef") || (ea.type == "copSubElement")
    }).each(function(ea) {
        var s = ea.getSourceCode();
        var m = s.match(/(cop\.with(?:out)?Layers)\((\[.*\])/)
        if (m) {
            var p = ea.getOwnerPath();
            layerUsage.push([m[1], m[2], p.first().fileName, p[p.length - 2].name + ">>" +  ea.name])
        }
        m = s.match(/(setWith(?:out)?Layers)\((\[.*\])/)
        if (m) {
            var p = ea.getOwnerPath();
            layerUsage.push([m[1], m[2], p.first().fileName, p[p.length - 2].name + ">>" +  ea.name])
        }
        m = s.match(/([A-Za-z0-9]+\.)?(beGlobal)\(/)
        if (!m) m = s.match(/(\.)(beGlobal)/)
        if (m) {
            var p = ea.getOwnerPath();
            layerUsage.push([m[2], m[1], p.first().fileName, p[p.length - 2].name + ">>" +  ea.name])
        }
    })

    var layerUsageTests = layerUsage.select(function(ea) {
        return ea[2].match(/^cop/)
    })

    this.tables["COPUsage"] =
        [["Activation", "LayerName", "Source", "ClassAndMethod"]].concat(
            layerUsage.withoutAll(layerUsageTests))

     this.tables["COPUsageTests"] =
        [["Activation", "LayerName", "Source", "ClassAndMethod"]].concat(layerUsageTests)


}

