function computeStatsAllLayersFull() {
    // l = allLayers.detect(function(ea) { return ea.name == "GraffleLayer"})


    // COP Layers
    var allLayers = this.allASTElements.select(function(ea) {
        return ea.type == "copDef"
    })

    var table = [["LayerName", "Class", "Method", "SourceSize", "Proceed", "Override", "FileName"]]
    allLayers
        .reject(function(ea) {
            return ea.getOwnerPath().first().fileName.match("cop")
        })
        .each(function(ea, index) {
            ea.subElements(0).each(function(eaClass) {
                eaClass.subElements(0).each(function(eaMethod) {
                    var s = eaMethod.getSourceCode();
                    var p = s.match("proceed")
                    try {
                        var classObj = eval(eaClass.name)
                    } catch(e) {}
                    if (classObj &&  classObj.prototype) {
                        var orgMethod = classObj.prototype[eaMethod.name]
                        if (orgMethod) orgMethod = orgMethod.originalFunction || orgMethod
                    } else if (classObj) {
                        // object specific
                        orgMethod = classObj[eaMethod.name]
                    }
                    var override = (orgMethod && (orgMethod  !== Functions.Null));
                    if (! classObj) override = 'undefined';
                    table.push([
                        ea.name, eaClass.name, eaMethod.name, s.length, p ? "true" : "false", override, ea.getOwnerPath().first().fileName])
                })
            })
        }, this)
    this.tables["AllLayersFull"] = table

}

