function computeWebwerkstattStats() {
    // egrep  -o 'cop.create\(.*?\)'  *xhtml */*xhtml */*/*xhtml */*/*/*xhtml */*/*/*/*xhtml | tee ~/Dropbox/Thesis/Webwerkstatt/COP13/webwerkstatt_cop_create.list
    var url = URL.root.withFilename("/users/jenslincke/thesis/COP13/webwerkstatt_cop_create.list")

    var content = url.asWebResource().get().content
    var table = []

    var lastFile;
    Strings.lines(content).each(function(ea) {
        var columns = ea.split(".xhtml:")
        if (columns.length == 1) {
            table.push([lastFile, columns[0]])
        } else {
            var fileName = columns[0] + ".xhtml"
            table.push([fileName, columns[1]])
            lastFile = fileName
        }
    })
    table = table.reject(function(ea) {
        return ea[1].match("__LivelyClassName__")
    })


    // reduce it first
    var h = this.histogram(table, function(ea) {
      return ea[0]+":" +  ea[1]
    })
    table = h.collect(function(ea) {
        return [ea.value, ea.key.split(":")[0], ea.key.split(":")[1]]
    }).collect(function(ea) {
        var path = ea[1].split("/")
        var world = path.pop().replace(".xhtml", "")
        var project = path.join("/")
        var layer = ea[2].replace(/.*?["']/,"").replace(/[\\"'].*/,"")
        return [ea[0], project, world, layer]
    }).reject(function(ea) {
        return ea[1].match("users/jenslincke/thesis/")
    }).select(function(ea) {
        return ea[3].match(/^[A-Za-z\.0-9]+$/)
    })

    var allLayers = this.allLayerNames();
    table.each(function(ea) { ea.push(allLayers.include(ea[3])) })

    this.tables["WebwerkstattCreate"] = [["Count", "Project", "World", "LayerName", "Known"]].concat(table)

    this.tables["WebwerkstattCreateDev"] = [["Count", "Project", "World", "LayerName", "Known"]].concat(
        table.select(function(ea) { return ! ea[4]}))


}

