function asLatexTable(table, label, caption) {
     var columnFormat = table[0].collect(function(ea) { return " l "}).join("") + ""


     return "\\begin{table}[htb!]\n" +
        "\\centering" +
        "\\begin{tabular}{" + columnFormat +"}\n" +
        // "\\hline\n" +
        Strings.lines(Strings.printTable(table, {separator: "&"})).join("\\\\\n") + "\\\\\n" + 
        // "\\\\ \\hline" + "\n" +
        "\\end{tabular}\n" +
        (caption ? ("\\caption{" + caption +"}\n") : "") +
        (label ? "\\label{" + label +"}\n" : "") +
        "\\end{table}"
}
