function showLastSelection() {

    if(!this.textSelectionMarker)
        this.textSelectionMarker = lively.morphic.Morph.makeRectangle(new Rectangle(0,0,10,10))


    this.textSelectionMarker.ignoreEvents()
    this.textSelectionMarker.isEpiMorph = true;
    this.textSelectionMarker.setOpacity(0.1)
    this.textSelectionMarker.setBounds(this.getLastTextSelectionBounds())
    this.world().addMorph(this.textSelectionMarker)
}

