function showNext() {
    var regEx = new RegExp(this.get("RegEx").textString, "g")
    regEx.lastIndex = this.lastIndex
    var s = this.get("ReplaceString").textString

    var text = this.getLastTextMorph()

    var m = regEx.exec(text.textString)
    if (!m) {
        this.get("Log").textString = "no match for '" + this.get("RegEx").textString + "'"
        this.lastIndex = 0
        this.lastM = null
        return
    }
    this.lastM = m
    var from = m.index
    var to = from + m[0].length
    if (to == this.lastIndex) {
        this.lastIndex = 0
    } else {
        this.lastIndex = to
    }
    text.setSelectionRange(from,to)

}

252.source:
function replaceNext() {
    var text = this.getLastTextMorph()
    var range = this.getLastTextSelectionRange()
    text.setSelectionRange(range[0],range[1])

    var s = this.get('ReplaceString').textString
    if (this.lastM) {
        s = s.replace("$1", this.lastM[1])
        s = s.replace("$2", this.lastM[2])
        s = s.replace("$3", this.lastM[3])
        s = s.replace("$4", this.lastM[4])
        s = s.replace("$5", this.lastM[5])
        s = s.replace("$6", this.lastM[6])
        s = s.replace("$7", this.lastM[7])
        s = s.replace("$8", this.lastM[8])
        s = s.replace("$9", this.lastM[9])
        s = s.replace("$10", this.lastM[10])
    }
    text.insertAtCursor(s, true, true);
    text.fixChunks()
    RememberTextSelectionLayer.lastTextSelectionRange = range

    this.showNext()
}

