194.source:
function installLayer() {
    cop.create("RememberTextSelectionLayer").refineClass(lively.morphic.Text, {
    setSelectionRange: function(a, b) {
        var r = cop.proceed(a, b)
        this.remeberTextSelection()
        return r
    },
    remeberTextSelection: function() {
        // use the layer as a singleton
        try {
            RememberTextSelectionLayer.lastTextMorph = this;
            RememberTextSelectionLayer.lastTextSelectionRange = this.getSelectionRange();
            // RememberTextSelectionLayer.lastTextSelectionBounds = this.getSelectionBounds();
            // RememberTextSelectionLayer.lastTextSelection = this.selectionString();
        } catch(e) {
            // ignore
        }
    },
    onMouseUp: function(evt) {
        var r = cop.proceed(evt)
         this.remeberTextSelection()
        return r
    }
})
    RememberTextSelectionLayer.sourceModule = null
    RememberTextSelectionLayer.doNotSerialize = ["lastTextMorph", "lastTextSelectionRange", 
        "lastTextSelectionBounds", "lastTextSelection"]
    this.setWithoutLayers([RememberTextSelectionLayer])
    RememberTextSelectionLayer.beGlobal()
}

