function exec(cmdString) {
    module('server.nodejs.WebInterface').load(true);

    if (!this.wd)
        this.wd = "/Users/jens/Dropbox/" 
    cmdString = (cmdString || this.get('inputLine').textString);

    var cmdExec = new CommandLineServerInterface();
    cmdExec.serverURL = new URL("http://localhost:18086/")
    connect(cmdExec, 'result', this,  'serverResult', {converter: function(ea) { return String(ea.stdout) }})
    this.log(this.wd + "$ " + cmdString)
    cmdExec.runCommand("cd " + this.wd + ";" + cmdString)
this
}

