Object.subclass('WordCompletion', {
	completeAll: function(prefix) {
		if (this.wordList === undefined) return [];
		return this.wordList.select(function(ea) { return ea.startsWith(prefix) });
	},
	complete: function(prefix) {
		var lowerCasePrefix = prefix.toLowerCase(),
			list = this.completeAll(lowerCasePrefix).sort(function(a,b) {
				return a.length == b.length ? (a < b ? -1 : +1) : a.length - b.length;
			});
		if (list.length === 0) return undefined;
		return lowerCasePrefix != prefix ? list[0].capitalize() : list[0];

	},
	loadWordList: function() {
		var dictUrl = 'http://lively-kernel.org/repository/webwerkstatt/marko/res/combinedWords.json',
			wordResource = new WebResource(dictUrl);
		this.wordList = JSON.unserialize(wordResource.get().content)
	}
});
Object.extend(WordCompletion, {
	current: function() {
		// offline support
		var world = WorldMorph.current();
		if (world.wordCompletion === undefined)
			world.wordCompletion = new WordCompletion();
		return world.wordCompletion;	
	}
});
