function dropSlides() {
    var m = this.get('Container');
    var w = this.world();
    var pos = pt(50,50)
    m.submorphs.clone().forEach(function(ea){
        w.addMorph(ea);
        ea.setPosition(pos)
        pos = pos.addPt(pt(0, 20 + ea.getExtent().y))
        ea.setScale(1);
    })
}
