function onSaveWorld() {
    var world = this.get('WorldContainer').submorphs[0]
    if(!world) {
        alertOK('no world to save!'); 
        return
    }
    var url = this.getWorldURL(),
        wr = new WebResource(url),
        doc = wr.get().contentDocument;
    world.remove()
    try {
        this.saveExternalWorld(url, world, doc)
    } finally {
        this.get('WorldContainer').addMorph(world)
    }
}

