function saveExternalWorld(url, world, doc) {
    var serializer = ObjectGraphLinearizer.forNewLively(),
        start = new Date().getTime();
    lively.persistence.Serializer.serializeWorldToDocumentWithSerializer(world, doc, serializer);


    var titleTag = doc.getElementsByTagName('title')[0];
        if (titleTag) titleTag.textContent = url.filename().replace('.xhtml', '');

    new WebResource(url).beAsync().put(doc)
}

