function createNode(url) {

    var node = Morph.makeRectangle(0,0,120,120);
    node.isLivelyWorldNode = true;
    node.applyStyle({fill: Color.gray, toolTip: url})

    node.url = url;

    this.loadImageForNode(node)

    var label = new lively.morphic.Text(new Rectangle(0,0,100,15), url.filename().truncate(18));
    label.beLabel();
    node.addMorph(label)
    node.setClipMode('hidden')

    label.align(label.bounds().bottomLeft(), node.shape.bounds().bottomLeft())
    return node
}

