function addNodes() {
    var urls = new WebResource(this.get('OverviewURL').textString)
        .getSubElements(10).subDocuments.invoke('getURL')
        .select(function(ea) {
            return ea.filename().match(/.xhtml$/) })
    urls.nestedDelay(function(ea) {
        var node = this.createNode(ea)
        this.get('ImageContainer').addMorph(node);
        this.layoutNodes()
    }.bind(this), 0.1)

}

