function layoutNodes() {

    var nodes  = this.getNodes();

    var columns = 5;
    var columnWidth = 130;
    var rowWidth = 130;
    var column = 0;
    var row = 0;
    var offset = pt(10,10);
    nodes.forEach(function(ea) {
        ea.setPosition(pt(columnWidth * column, rowWidth * row).addPt(offset))
        column++;
        if (column >  columns) {
            column = 0;
            row++;
        }
    })

}

