function loadImageForNode(node) {
    var screenshotService = 'http://lively-kernel.org/nodejs/PhantomJSServer/screenshot/thumb/'
    // var screenshotService = 'http://lively-kernel.org/nodejs/PhantomJSServer/screenshot'
// medium, cropped, thumb, part (size)

    var url = node.url
    var path = url.pathname
    var imageURL = new URL(screenshotService + path)
    var img = new lively.morphic.Image(new  Rectangle(0,0,100,100), imageURL, false)
    node.addMorph(img)
}

