159.source:
function updateItemMorphs() {
    // this.setList(["A", "B", "Hello\nWorld"])
    var container = this.get("Container");
    container.submorphs.invoke('remove')
    if (! this.itemList ) return;
    var itemStrings = this.itemList.collect(function(ea) { 
        return this.renderFunction(ea); }, this);
    var itemWidth = this.getExtent().x - 20;
    itemStrings.each(function(ea, index) {
        var itemMorph = new lively.morphic.Text(new Rectangle(0,0,itemWidth, 10), ea);
        itemMorph.applyStyle({fill: null, borderWidth: 0, fixedHeight: false});
        itemMorph.fit();
        itemMorph.item = this.itemList[index]
        itemMorph.addScript(function onMouseWheel() {
            return false
        })
        container.addMorph(itemMorph)
    }, this)
}

