function updateList(items) {
    if (!items) items = [];
    this.itemList = items;

    this.updateItemMorphs()
}

171.source:
function onMouseDown(evt) {
    if (this.selectedMorphs) this.selectedMorphs.forEach(function(ea) {
        ea.setFill(null)
    })
    var c = this.get('Container');
    var scroll = this.getScroll()

    var p = c.localize(evt.mousePoint.scaleBy(1 / $world.getScale())
        .addPt(pt(scroll[0], scroll[1])))

    // show(evt.mousePoint)
    this.selectedMorphs = c.submorphs.select(function(ea) {
        return ea.bounds().containsPoint(p)
    });

    this.selectedMorphs.forEach(function(ea) {
       ea.setFill(Color.gray.withA(0.5))
       this.selection = ea.item.value || ea.item
    }, this)

    return true
}

