function search(s) {
    var query = {
        from: 0, size: 100
    }
    query.query =  {text_phrase: {
        textString:  s 
    }}
    var content = this.searchWorld(query);
    if (!content) {
         this.get('ResultList').setList(['no access to search']);
         return
    };
    var result = JSON.parse(content)
    // "1.aaa\n2.aa\n3.aaaa\n4.aaa\abc\n5.c\n6.c\n7.ccc".match(/[^\n]+\n[^\n]*b[^\n]*\n?[^\n]*?/)

   this.get('ResultList').setList(result.hits.hits.collect(function(ea) {
        // extract
        var snippet = ""
        var full = ea._source.textString || ea._source.source;
        if (full)  {
            snippet = full.truncate(600)
        } else {
            var snippet = "no testString"
        }
        return {
            string: this.idToURL(ea["_id"]).relativePathFrom(URL.root)  
                       + (snippet  ? ":\n" + snippet.replace(/\n$/,"") : ""),
            value: ea
        }
    }, this))
    this.get('Status').textString = result.hits.total + " hits   "
    this.get('ResultList').get("Container").submorphs.each(function(ea) {
        ea.emphasizeRegex(new RegExp(s, "g"), {color: Color.red})
        ea.setFontSize(8);
        ea.emphasizeRegex(new RegExp("^.*.xhtml:", "g"), {fontSize: 10, fontWeight: 'bold', color: Color.blue})
        ea.fit()
    })
    this.get('ResultList').layout()
}

