function visitWorld() {
    var selection = this.get('ResultList').selection
    if (!selection) return;

    // convention parse id to xhtml url
    var url = this.idToURL(selection._id);

    var path = selection._id.split("$")[1];

    window.open(url + "?worldSearch=" + path + "$" +
        this.get("SearchField").textString)
}

