function onDragStart(evt) {
    this.dragStartPoint = evt.mousePoint;
    this.morphA = this.getSiblingsAtCorner("topLeft")[0]
    this.morphB = this.getSiblingsAtCorner("bottomLeft")[0]

    if (!this.morphA || !this.morphB) return;

    this.originalMorphAExtent = this.morphA.getExtent();
    this.originalMorphBExtent = this.morphB.getExtent();
    this.originalMorphBPosition = this.morphB.getPosition();

}

