function morphToSearchDocuments(morph, visited, path, result){
    // beware of recursion
    visited = visited || [];
    if (visited.include(morph)) return;
    visited.push(morph)

    result = result || [];
    path = path || [];

    var obj = {};
    obj.path = path.join(".");
    if (morph.textString) {obj.textString = morph.textString};
    if (morph.name) {obj.name = morph.name};
    if (morph.id) {obj.id = morph.id};
    if (morph.derivationIds) {obj.derivationIds = morph.derivationIds};

    if (morph.submorphs && morph.submorphs.length > 0) {
        morph.submorphs.forEach(function(ea, index) {
            this.morphToSearchDocuments(ea, visited, path.concat(["submorphs", index]), result)
        }, this)
    }
    result.push(obj); // gather results here
    return result;
}

