function updateSearchIndex(url, depth, endFunc) {
    this.log("updateSearchIndex " + url)
    var worldURLs = this.findWorlds(url, depth)
    var length = worldURLs.length
    worldURLs.doAndContinue(function(next, ea, index) {
        var webR = ea.asWebResource();
        this.get('ProgressBar').setValue(1 - (index / length))
        this.get('ProgressBar').setLabel(ea.filename())

        var id = this.urlToID(ea)
        this.log("updated " + id)    
        if (this.get('DryRun').isChecked()) {
            next.delay(0.05)
            return
        }
        lively.bindings.connect(webR, 'contentDocument', {call: function(xml) {
            try {
                var world = lively.morphic.World.fromDocument(xml);
                this.morphToSearchDocuments(world).forEach(function(ea) {
                    this.putWorld(id + "$" + ea.path, ea)
                }, this)
            } catch(e) {
                this.log("problems updating " + id)
            } finally {
                next()
            }
        }.bind(this)}, 'call')
        webR.beAsync()
        webR.get()
    }.bind(this), function() {
        this.get('ProgressBar').setValue(1)
        this.get('ProgressBar').setLabel("finished")
        endFunc && endFunc()
    }.bind(this))
}

