function updateRecursive(url, nextFunc) {
    var relativeURL = url.relativePathFrom(URL.root)
    this.log("recursive update " +  relativeURL)

    this.updateSearchIndex(url, 1, function() {
        url.asWebResource().getSubElements(1).subCollections.invoke('getURL')
            .doAndContinue(
                function(next, ea) {
                    this.updateRecursive(ea, next)
                }.bind(this),
                function() {
                    this.log("finised " + relativeURL)
                    nextFunc && nextFunc()
                }.bind(this))
    }.bind(this))

}

