410.source:
function installLayers() {
    cop.create("NoPrettyArrayPrintingLayer").refineObject(Global, {
        printOn: function(objOrArray, ws) {
            // work around OMeta and D3 array printing incompatibilities
            if (Object.isArray(objOrArray)) {
                for (var idx = 0; idx < objOrArray.length; idx++) {
                    if (idx > 0)
                    ws.nextPutAll(", ")
                    printOn(objOrArray[idx], ws);
                }
            } else {
               return cop.proceed(objOrArray, ws)
            }
        }
    })
}

418.source:
function draw() {
    this.clearChart()

    var width = this.getExtent().x,
        height = this.getExtent().y;


    var diameter = height * ((0.5) * 2 ),
        radius = diameter / 2,
        innerRadius = radius - 120;

    var cluster = d3.layout.cluster()
      .size([360, innerRadius])
      .sort(null)
      .value(function(d) { return d.size; });


    var tree = d3.layout.tree()
       .size([360, (diameter / 2 - 120) * this.scaleDetail * 100])
       .separation(function(a, b) { return (a.parent == b.parent ? 1 : 2) / a.depth; });

    var diagonal = d3.svg.diagonal.radial()
       .projection(function(d) { return [d.y, d.x / 180 * Math.PI]; });

    var self = this;


    var svgOuter = this.chart.append('svg')
       .attr('width', width)
       .attr('height', height)
       .call(d3.behavior.zoom().on("zoom",  function() {
            self.lastD3Scale = d3.event.scale;
            self.lastD3Tanslate = d3.event.translate;
            svg.attr("transform","translate(" + d3.event.translate[0]  + ","
                +  d3.event.translate[1] +
                ") scale(" +  d3.event.scale + ")");
        }))
       .append("g")
           .attr("transform", "translate(" + radius + "," + radius + ")")


    var svg = svgOuter.append ("g");

     if (self.lastD3Scale &&  self.lastD3Tanslate) {
            svg.attr("transform","translate(" + self.lastD3Tanslate[0]  + ","
                +  self.lastD3Tanslate[1] +
                ") scale(" +  self.lastD3Scale + ")");
    }



    var bundle = d3.layout.bundle();

    var nodes =tree.nodes(this.data.root), 
      links = tree.links(nodes);
    // var nodes = cluster.nodes(this.data.root)
    // links = tree.links(nodes);

    var self = this;

    cop.withLayers([NoPrettyArrayPrintingLayer], function() {
        var link = svg.selectAll(".link")
          .data(links)
          .enter()
          .append("path")
            .attr("class", "link")
            .attr("d", diagonal);

        var line = d3.svg.line.radial()
           .interpolate("bundle")
           .tension(.85)
           .radius(function(d) { return d.y; })
           .angle(function(d) { return d.x / 180 * Math.PI; });


        svg.selectAll(".link2")
           .data(bundle(this.data.connections))
           .enter().append("path")
             .attr("class", "link2")
             .attr("d", line)



    }.bind(this))

     var node = svg.selectAll(".node")
     .data(nodes)
   .enter().append("g")
     .attr("class", "node")
     .attr("transform", function(d) { return "rotate(" + (d.x - 90) + ")translate(" + d.y + ")"; })

     node.append("circle")
     .attr("fill", function(d) {
            return  Color.white
     })
     .attr("r", function(d) {
        return  (d.collapsedParents ? Math.sqrt(d.collapsedParents.length) : 1)  * 2 + 1 } )

     node.append("text")
     .attr("dy", ".31em")
     .attr("text-anchor", function(d) { return d.x < 180 ? "start" : "end"; })
     .attr("transform", function(d) {
        return d.x < 180 ? "translate(8)" : "rotate(180)translate(-8)"; })
     .text(function(d) { return d.name.truncate(30, ""); })
     .on("click", function(d) {
        try {
            self.owner.selectNode(d)
        } catch(e) {$world.logError(e)}}.bind(this));


    }

