694.source:
function fetchData() {
    this.data = {
        morphs: {},
        nodesByURL: {},
        nodesByID: {},
        connections: [] }
    this.data.root = {name: '', children: []}

    var parts = Properties.values(this.get("WorldStatsPane").parts).pluck('object')

    parts.each(function(ea) {
        if (!ea || !ea.id) return;
        if (ea)
            this.addDerivationHistory((ea && ea.derivationIds ||[]).concat([ea.id]))
        var node = this.data.nodesByID[ea.id]
        if (node) {
            node.name = (''+ea.id).truncate(4,"") +"_" + ea.name
            node.size = 10
        }
    }, this)

    this.get('D3Panel').data = this.data
    this.get('D3Panel').draw()
}

706.source:
function addDerivationHistory(list, optParentID) {
    // this.fetchData()
    if (list.length == 0) return;
    var id = list.shift();
    if (this.data.nodesByID[id]) {
        return this.addDerivationHistory(list, id) 
    }
    var node = {name: (""+id).truncate(6,""), id:""+id, children: []}
    this.data.nodesByID[id] = node


    var parentNode = optParentID ? this.data.nodesByID[optParentID] : this.data.root
    parentNode.children.push(node)
    this.addDerivationHistory(list, id) 
}

