714.source:
function collapseDepenciesListsInNode(node) {
    if (node.children.length == 0) return
    if (node.children.length > 1) {
        node.children.each(function(ea) {
            this.collapseDepenciesListsInNode(ea) }, this)
        return
    }

    var child = node.children[0]
    if (node.collapsedParents) 
        child.collapsedParents = node.collapsedParents.concat([node])
    else
        child.collapsedParents = [node]
    // replace ea with its child in node
    var index = node.parent.children.indexOf(node)
    node.parent.children[index] = child
    child.parent = node.parent
    this.collapseDepenciesListsInNode(child)

}

