722.source:
function unfoldCollapsedParentsInNode(node) {

    if (node.collapsedParents) {
        node.collapsedParents.each(function(parent) {

            var index = node.parent.children.indexOf(node)
            node.parent.children[index] = parent
            parent.parent = node.parent

            parent.children = [node]
            node.parent = parent
            delete parent.collapsedParents
        })

       delete node.collapsedParents
    }

    if (node.children.length == 0) return
    node.children.each(function(ea) {
        this.unfoldCollapsedParentsInNode(ea) }, this)






}

