2713.source:
function createKeys() {
    this.submorphs.invoke('remove')
    var keysize = 80,
        keyoffset = 6,
        boardoffset = 20;
    var row = 0, column = 0;
    var layout = this.getLayout();
    var maxcol = 0;
    for (row = 0; row < layout.length; row++) {
        for(column = row; column < layout[row].length+row; column++) {
            var key = this.makeKey(layout[row][column-row]);
            key.setExtent(pt(keysize,keysize));
            key.setPosition(pt(
                boardoffset - row*0.5*keysize + column*keysize + column * keyoffset,
                boardoffset + row*keysize + row * keyoffset
            ))
            if (maxcol < column) maxcol = column
        }   
    }
    this.setExtent(pt(
        (maxcol+1)* keysize + maxcol * keyoffset + boardoffset*2,
        layout.length * keysize + layout.length * keyoffset + boardoffset*2
    ))
}

2717.source:
function makeKey(string) {
    var key = new lively.morphic.Button;
    key.setBorderRadius(10)
    this.addMorph(key);
    key.setLabel(string == ' '? '␣' : string);
    key.storedString = string;
    key.label.setFontSize(40)
    key.addScript(function onFire() {
        if (this.owner.target && this.owner.target.setTextString) {
            this.owner.target.setTextString(
                this.owner.target.getTextString() + this.storedString
            )
        }
        else {
            alert('target '+this.owner.target+'is not an input field. Please use "setTarget" on the keybard.')
        }
    })
    connect(key, 'fire', key, 'onFire')
    return key;
}

