2834.source:
function installLayers() {
    cop.create("NoPrettyArrayPrintingLayer").refineObject(Global, {
        printOn: function(objOrArray, ws) {
            // work around OMeta and D3 array printing incompatibilities
            if (Object.isArray(objOrArray)) {
                for (var idx = 0; idx < objOrArray.length; idx++) {
                    if (idx > 0)
                    ws.nextPutAll(", ")
                    printOn(objOrArray[idx], ws);
                }
            } else {
               return cop.proceed(objOrArray, ws)
            }
        }
    })

   // d3.transition.prototype.attr
   console.log("INSTALL D3 Error handler")
   var errorHandlerFunc = function(key, value) {
            if (Object.isFunction(value)) {
                return cop.proceed(key, function() {
                    try {
                        return value.apply(this, arguments)
                    } catch(e) {
                        $world.logError(e)
                    }
                })
            }
            return cop.proceed(key, value)
        }
    // Damn it, it error handling seems to be a homogenous concern
    // and AOP would be more appropriate here
    // but, higher order functions are also viable
    cop.create("CaptureD3Errors").refineClass(d3.transition, {
        attr: errorHandlerFunc,
        style: errorHandlerFunc,
    }).refineClass(d3.selection, {
        attr: errorHandlerFunc,
        style: errorHandlerFunc,
    }).beGlobal()




}

2842.source:
function draw() {
    if (!this.chart) this.chart = this.getChart();
    this.chart.selectAll('svg').remove();

    var width = this.getExtent().x,
        height = this.getExtent().y;

    var diameter = height,
        radius = diameter / 2,
        innerRadius = radius - 120;

    var cluster = d3.layout.cluster()
      .size([360, innerRadius])
      .sort(null)
      .value(function(d) { return d.size; });


    var tree = d3.layout.tree()
       .size([360, diameter / 2 * Math.log(this.scaleDetail)  - 120])
       .separation(function(a, b) { return (a.parent == b.parent ? 1 : 2) / a.depth; });

    var diagonal = d3.svg.diagonal.radial()
       .projection(function(d) { return [d.y, d.x / 180 * Math.PI]; });

    this.zoomBehavior = d3.behavior.zoom().on("zoom", 
        function() {this.onD3Zoom(this)}.bind(this))

    var svgOuter = this.chart.append('svg')
       .attr('width', width)
       .attr('height', height)
       .call(this.zoomBehavior)
       .append("g")
           .attr("transform", "translate(" + radius + "," + radius + ")")


    var svg = svgOuter.append ("g");
    this.svgNode = svg


    var bundle = d3.layout.bundle();

    var nodes =tree.nodes(this.data.root),
      links = tree.links(nodes);

    var self = this;
    cop.withLayers([NoPrettyArrayPrintingLayer], function() {
        var link = svg.selectAll(".link")
          .data(links)
          .enter()
          .append("path")
            .attr("class", "link")
            .attr("d", diagonal);

        var line = d3.svg.line.radial()
           .interpolate("bundle")
           .tension(.85)
           .radius(function(d) { return d.y; })
           .angle(function(d) { return d.x / 180 * Math.PI; });


        svg.selectAll(".link2")
           .data(bundle(this.data.connections))
           .enter().append("path")
             .attr("class", "link2")
             .attr("d", line)

    }.bind(this))

     var node = svg.selectAll(".node").data(nodes)
        .enter().append("g")
        .attr("class", "node")
        .attr("transform", function(d) { 
            return "rotate(" + (d.x - 90) + ")translate(" + d.y + ")"; })

    node.append("circle")
        .attr("class", "nodeShape")
        .on("mouseover", function(d) { this.highlightMorph(d.id)}.bind(this));

    node.append("text")
        .attr("class", "nodeLabel")

    this.updateTextLabels()
    this.updateNodeStyle()
    this.updateDerivationDepthVis()

    }

