2858.source:
function addMorphsAndSubmorphLinks() {
    this.data = {morphs: {}, connections: []}
    var self = this;
    var createTree = function(m) {
        var node = {
            name: m.name || m.constructor.name, 
            derivationIds: (m.derivationIds || []).concat([m.id]),
            partsBinMetaInfo: m.partsBinMetaInfo,
            type: m.constructor.type,
            id: m.id}
        node.ownProperties = Properties.own(m).length
        node.ownFunctions = Functions.own(m).length
        self.data.morphs[node.id] = node
        if (m.submorphs) {
            node.children = m.submorphs.select(Functions.K).collect(function(ea) { 
                var childNode = createTree(ea)
                childNode.parent = node
                return childNode
            })
        }
        return node
    }
    this.data.root = createTree(this.target)


}

2862.source:
function addDerivationHistoryOfMorph(m, depth) {
    var source = m
    if (!source || !source.derivationIds) return;


    var morphs = Properties.values(this.data.morphs)

    var blueRect = $world.loadPartItem("Rectangle","PartsBin/Basic")
    var button = $world.loadPartItem("Button","PartsBin/Inputs")

    if (m.name && m.name.match(/Button/)) return;


    // this.lastNode.derivationIds
    // m = this.lastNode
    // c = this.data.connections.detect(function(ea) { 
    //  return ea.source === m || ea.target === m})


    source.derivationIds.concat([source.id]).each(function(id) {
        var target;
        if (id == null) return;
        if (blueRect.derivationIds.include(id)) return;
        if (button.derivationIds.include(id)) return;

        morphs.each(function(ea) {
            if (!ea || !ea.id) return;
            if(ea.id == id || (ea.derivationIds && ea.derivationIds.include(id))) { 
                target = ea
            }
        })

        if (target.type == 'lively.morphic.Text') return; // ignore text morphs

        if (target && (target.id !== source.id)) {
            this.data.connections.push({
                source: this.data.morphs[source.id], 
                target: this.data.morphs[target.id], 
                derivationLink: id,
                depth: depth,
                weight: 0.5})
        }
    }, this)

}

