2878.source:
function updateDerivationDepthVis() {
    var maxDepth = Number(this.get('depth').textString)
    this.chart.selectAll(".link2")
        .attr("stroke", function(d, index) {
            var connection = this.data.connections[index]
            lastC = connection
            if (connection.depth > maxDepth  ) {
                return Color.green.withA(0.4)
            } else {
                return Color.blue.withA(0.4)
            }
            }.bind(this))
        .attr("stroke-width", function(d, index) {
            var connection = this.data.connections[index]
            lastConnection = connection
            if (connection.childConnections)
                 return 3 * Math.sqrt(connection.childConnections.length)
             else
                 return 1}.bind(this))
         .attr("opacity", function(d, index) {
            var connection = this.data.connections[index]
            if (connection.siblingConnections)
                 return 0.2 + (0.8  / Math.sqrt(connection.siblingConnections.length))
            else
                 return 0.2
            }.bind(this))
}

