2890.source:
function foldNonConnectedChilds() {
    this.data.morphs

    var connected = this.getConnectedMorphs()


    var leaves = Properties.values(this.data.morphs).select(function(ea) { return ea.children.length == 0})

    var root = this.data.root
    var nonConnected = leaves.withoutAll(connected)
    nonConnected = nonConnected.reject(function(ea) {
        return ea.parent == root || !ea.parent
    })

    nonConnected.each(function(ea) {
        if (!ea.parent.foldedChildren) {
            ea.parent.foldedChildren = []
        }
        ea.parent.foldedChildren.push(ea)
        ea.parent.children = ea.parent.children.without(ea)
        ea.parent = null
    })

    this.redraw()


}

2894.source:
function getConnectedMorphs() {
    return this.data.connections.inject([], function(all, ea) {
        all.push(ea.source)
        all.push(ea.target)
        return all
    }).uniq()
}

2898.source:
function unfoldNonConnectedChilds() {
    var morphsWithFoldedChildren = Properties.values(this.data.morphs).select(function(ea) { return ea.foldedChildren})

    morphsWithFoldedChildren.each(function(eaParent) { 
        eaParent.foldedChildren.each(function(eaChild) {
            eaParent.children.push(eaChild)
            eaChild.parent = eaParent
        })
        delete eaParent.foldedChildren
    })
    this.redraw()


}

