2914.source:
function bundleConnections() {

    var all = this.data.connections
    var uniqCons = []
    all.each(function(a) {
        if (! uniqCons.detect(function(b) { 
            return a.source == b.source && a.target == b.target }))
            uniqCons.push(a)
    })

    var childCons = this.data.connections.select(function(c) {
        var parentConnection =  all.detect(function(ea) {
            return ea.source == c.source.parent
                && ea.target == c.target.parent;
        })
        if (parentConnection) {
            if (!parentConnection.childConnections)
                parentConnection.childConnections = []
            parentConnection.childConnections.push(c)
            c.parentConnection = parentConnection
        }

        return parentConnection
    })

    this.data.connections = this.data.connections.withoutAll(childCons)

    this.redraw()
}

2918.source:
function redraw() {
