function makeUniqConnections() {
    var all = this.data.connections
    var uniqCons = []
    all.each(function(a) {
        var sibling = uniqCons.detect(function(b) { 
            return a.source == b.source && a.target == b.target })
        if (sibling) {
            if (!sibling.siblingConnections)
                sibling.siblingConnections = [];
            sibling.siblingConnections.push(a)
        } else {
            uniqCons.push(a)
        }
    })
    this.data.connections = uniqCons
}

